import { Component, OnInit } from '@angular/core';
import { UserDto } from '../dto/UserDto';
import { HttpClient } from '@angular/common/http';
import { ENDPOINTS } from '../rest-endpoint';

@Component({
  selector: 'app-manage',
  templateUrl: './manage.component.html',
  styleUrls: ['./manage.component.css']
})
export class ManageComponent implements OnInit{

  existingUsers: UserDto [] = []

  constructor (private http:HttpClient){}


  ngOnInit(): void {
    this.http.get<UserDto[]>(ENDPOINTS.GETALL).subscribe(
      (data) => {
        this.existingUsers = data;
        console.log(this.existingUsers);
      }
    )
  }

}
<div class="content">
    <div class="manageTable">
        <h2>Manage Users</h2>
        <table class="table table-striped table-dark">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">User Name</th>
                <th scope="col">Email</th>
                <th scope="col">Name</th>
                <th scope="col">Age</th>
                <th scope="col">Birthday</th>
                <th scope="col">Account Type</th>
                <th scope="col">Edit</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let row of existingUsers">
                <th scope="row">1</th>
                <td>{{row.userName}}</td>
                <td>{{row.email}}</td>
                <td>{{row.name}}</td>
                <td>{{row.age}}</td>
                <td>{{row.dob}}</td>
                <td>
                    <label *ngIf="row.adminAcc">ADMIN</label>
                    <label *ngIf="!row.adminAcc">ADMIN</label>
                </td>
                <td><i class="bi bi-pencil-square"></i></td>
              </tr>
            </tbody>
          </table>
    </div>
</div>
