import { Component, OnInit } from '@angular/core';
import { UserDto } from '../dto/UserDto';
import { HttpClient } from '@angular/common/http';
import { ENDPOINTS } from '../rest-endpoint';
import { DataService } from '../services/data.service';
import { ModalDismissReasons, NgbDatepickerModule, NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-manage',
  templateUrl: './manage.component.html',
  styleUrls: ['./manage.component.css']
})
export class ManageComponent implements OnInit{

  userForm!:FormGroup;
  existingUsers: UserDto [] = [];
  userData!: UserDto;
  userExist: boolean = false;

  constructor (
    private http:HttpClient, 
    private modalService: NgbModal, 
    private fb:FormBuilder,
    ){
      this.userForm = this.fb.group({
        userName: ['', Validators.required],
        password: ['', Validators.required],
        email: ['', [Validators.required, Validators.email]],
        name: ['', Validators.required],
        age: ['', [Validators.required, Validators.min(1)]],
        dob: ['', Validators.required],
        Admin:[false] //When updating user accounts we cant change account type so this value has no effect
      });
    }


  ngOnInit(): void {
    this.http.get<UserDto[]>(ENDPOINTS.GETALL).subscribe(
      (data) => {
        this.existingUsers = data;
        console.log(this.existingUsers);
      }
    )
  }

  editUser(userData: UserDto){
    this.userData=userData;

    this.userForm.get('userName')?.setValue(userData.userName);
    this.userForm.get('password')?.setValue(userData.password);
    this.userForm.get('email')?.setValue(userData.email);
    this.userForm.get('name')?.setValue(userData.name);
    this.userForm.get('age')?.setValue(userData.age);
    this.userForm.get('dob')?.setValue(userData.dob);   

  }

  open(content: any) {
		this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title' });
	}

  onSubmit(user:UserDto){
    this.http.get(ENDPOINTS.CHECK+user.userName).subscribe(
      (data) => {
        if (!data || (this.userData.userName==user.userName)){
          this.http.put<UserDto>(ENDPOINTS.UPDATE,user).subscribe(
            (response)=> {
              this.userData=response;
          })
        }
        else{
          this.userExist=true;
        }
      }
    )   
  }

}
<div class="content">
    <div class="manageTable">
        <h2>Manage Users</h2>
        <table class="table table-striped table-dark">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">User Name</th>
                <th scope="col">Email</th>
                <th scope="col">Name</th>
                <th scope="col">Age</th>
                <th scope="col">Birthday</th>
                <th scope="col">Account Type</th>
                <th scope="col">Edit</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let row of existingUsers">
                <th scope="row">1</th>
                <td>{{row.userName}}</td>
                <td>{{row.email}}</td>
                <td>{{row.name}}</td>
                <td>{{row.age}}</td>
                <td>{{row.dob}}</td>
                <td>
                    <label *ngIf="row.adminAcc">ADMIN</label>
                    <label *ngIf="!row.adminAcc">USER</label>
                </td>
                <td><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pencil-square" viewBox="0 0 16 16" (click)="editUser(row)" (click)="open(content)">
                    <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                    <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                  </svg></td>
              </tr>
            </tbody>
          </table>
    </div>
</div>





<ng-template #content let-modal>
	<div class="modal-header">
		<h4 class="modal-title" id="modal-basic-title">Edit User {{userData.userName}}</h4>
		<!-- <button type="button" class="btn-close" aria-label="Close" (click)="modal.dismiss('Cross click')"></button> -->
	</div>
  <form [formGroup]="userForm" (ngSubmit)="onSubmit(userForm.value)">
	<div class="modal-body">


      <div class="form-group">
          <label for="userName">User Name</label>
          <input formControlName="userName" type="text" class="form-control">
          <label for="userName" *ngIf="userExist">User Name already exist</label>
      </div>
      <div class="form-group">
          <label for="userName">Password</label>
          <input formControlName="password" type="text" class="form-control">
      </div>
      <div class="form-group">
          <label for="email">Email</label>
          <input formControlName="email" type="text" class="form-control">
      </div>
      <div class="form-group">
          <label for="name">Name</label>
          <input formControlName="name" type="text" class="form-control">
      </div>
      <div class="form-group">
          <label for="age">Age</label>
          <input formControlName="age" type="number" class="form-control">
      </div>
      <div class="form-group">
          <label for="dob">Date of Birth</label>
          <input formControlName="dob" type="date" class="form-control">
      </div>   
	</div>

	<div class="modal-footer">
    <div class="button-container">
      <button type="submit" class="btn btn-primary">Save Changes</button>
      <button class="btn btn-error">Save Changes</button>
    </div>
	</div>
</form>
</ng-template>
